interface Handler {
  name: string;
  callback: Function;
  /**
   * é»˜è®¤ false
   */
  isOnce?: boolean;
  /**
   * é»˜è®¤ false
   */
  disabled?: boolean;
}

class Event {
  listeners: Handler[] = []

  on(name, callback) {
    this.listeners.push({
      name,
      callback,
    })
    return this
  }

  off(name, callback) {
    if (callback) {
      this.listeners = this.listeners.filter((item) => (
        (item.name !== name)
        || (item.callback !== callback)
       ))
      return this
    }
    this.listeners = this.listeners.filter((item) => (item.name !== name))
  }

  once(name, callback) {
    this.listeners.push({
      name,
      callback,
      isOnce: true,
    })
    return this
  }

  emit(name, value) {
    const listeners = this.listeners.filter((item) => (item.name === name) && (!item.disabled))
    for (let i = 0, len = listeners.length; i < len; i += 1) {
      const listener = listeners[i]
      listener.callback(value)
      if (listener.isOnce) {
        listener.disabled = true
      }
    }
  }
}
