function scramble(str1, str2) {
  let s1 = str1.split("").sort()
  let s2 = str2.split("").sort()
  let n = 0 
  s2.forEach(e=>{
    let index = s1.indexOf(e)
    if(index>-1){
      n++
      s1.splice(index,1)
    }
  })

  if(n==s2.length && n!=0){
    return true
  }else{
    return false
  }
}
